//#region react
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from "react-router";
import io from "socket.io-client";
//#endregion

//#region mui
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Card from '@mui/material/Card';
import CardMedia from '@mui/material/CardMedia';
import images1 from '../../images/house.jpg'
import FormControl from '@mui/material/FormControl';
import InputLabel from '@mui/material/InputLabel';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
//#endregion

//#region 라이브러리
import axios from 'axios';
import FormData from 'form-data';
import Web3 from 'web3';
//#endregion

import NFT from "../../../contracts/NFT.json";

//#region 전역 변수
let socket;
let NFT_Hash;
let web3;
let NFT_instance;
//#endregion

function HouseInfo_insert() {

  const ENDPOINT = "http://localhost:8080";

  //#region uesState 변수
  const [selluserId] = useState(location.state[0].id);
  const [sellusername] = useState(location.state[0].name);
  const [sellusernumber] = useState(location.state[0].number);
  const [sellerMetaAddress]=useState(location.state[0].MetaMaskAcc);

  const [area, setArea] = useState("");
  const [address, setAddress] = useState("");
  const [agree, setAgree] = useState(false);
  const [price, setPrice] = useState();

  const [files, setFiles] = useState(images1);

  const [file, setFile] = useState();
  const [accounts, setAccounts] = useState("");

  const navigate = useNavigate();
  const location = useLocation();
  //#endregion

  //#region useEffect
  useEffect(() => {
    async function load() {
      web3 = new Web3(Web3.givenProvider || 'http://localhost:8545');
      setAccounts(await web3.eth.getAccounts());

      const networkId = await web3.eth.net.getId();
      const NFTNetwork = NFT.networks[networkId];

      NFT_instance = new web3.eth.Contract(NFT.abi, NFTNetwork.address);
      console.log(NFT_instance);
    }

    load();
  }, []);

  useEffect(() => {
    socket = io(ENDPOINT);
  }, []);
  //#endregion

  //#region 이미지 업로드
  const handleFile = async (fileToHandle) =>{
    console.log('starting');

    const formData = new FormData();
    formData.append("file", fileToHandle);

    const filename = fileToHandle.name;      //파일 이름.확장자
    const filenameStr = filename.split('.'); //파일이름

    const url =  `https://api.pinata.cloud/pinning/pinFileToIPFS` //파일 올리는 URL

    const response = await axios.post(
      url,
      formData,
      {
          maxContentLength: "Infinity",
          headers: {
              'Content-Type': `multipart/form-data;boundary=${formData._boundary}`, 
              'pinata_api_key': '97f3182c5eaaa5c01af0',
              'pinata_secret_api_key': '23a31aa6f14b878f66c3f3b4f639f03e8619b1851e872d99793680db7550ae7b',
          }
      }
    );

    console.log(response);
    Make_Json(filenameStr[0], response.data.IpfsHash); //response.data.IpfsHash : 매물 해시값
  }
  //#endregion

  //#region 제이슨 파일 업로드
  const Make_Json= async(filenameStr, img_cid)=>{

    //#region JSON 변환
    var data = JSON.stringify({
      "pinataOptions": {
        "cidVersion": 1
      },
      "pinataMetadata": {
        "name": `${filenameStr}.json`,
        "Address" : "우송대학교",
        "Price" : "1",
        "keyvalues": {
          "customKey": "customValue",
          "customKey2": "customValue2"
        }
      },
      "pinataContent": {
        "img" : `ipfs://${img_cid}`,
        "description" : "위베어베어스",
      }
    });
    //#endregion

    var config = {
      method: 'post',
      url: 'https://api.pinata.cloud/pinning/pinJSONToIPFS',  //JSON 올리는 URL
      headers: { 
        'Content-Type': 'application/json', 
        'pinata_api_key': '97f3182c5eaaa5c01af0',
        'pinata_secret_api_key': '23a31aa6f14b878f66c3f3b4f639f03e8619b1851e872d99793680db7550ae7b',
      },
      data : data
    };

    const res = await axios(config);

    console.log(res.data);
    NFT_Hash = res.data.IpfsHash;  //JSON 해쉬 값
  }
  //#endregion

  //#region 이미지 인코딩
  const encodeFileToBase64 = (fileBlob) => {
    const reader = new FileReader();
    reader.readAsDataURL(fileBlob);

    return new Promise((resolve) => {
      reader.onload = () => {
        setFiles(reader.result);
        resolve();
      };
    });
  };
  //#endregion

  //#region 매물등록
  function House_register() {

    if (agree === false) {
      alert("개인정보 동의를 하세요");
    }
    else if (area === "" || address === "") {
      alert("입력하지 않은 정보가 있습니다");
    }
    else {
      socket.emit("House_Register", { area, address, price, files, selluserId, sellusername, sellusernumber, sellerMetaAddress, NFT_Hash });

      socket.on("House_Register_Result",  async (CheckMsg) => {
        await NFT_instance.methods.mintNFT(accounts[0], `ipfs://${NFT_Hash}`).send({
          from: accounts[0],
          gas: 5000000
        });
        alert(CheckMsg);
        navigate("/post-MainPage", { state: location.state });
      })
    }
  }
  //#endregion

  //#region 동의여부
  function CheckBoxBool() {
    if (agree === false) {
      setAgree(true);
    }
    else if (agree === true) {
      setAgree(false);
    }
  }
  //#endregion

  //#region 렌더링
  return (
    <Container component="main" maxWidth="sm" sx={{ mb: 4 }}>
      <Paper variant="outlined" sx={{ my: { xs: 3, md: 6 }, p: { xs: 2, md: 3 } }}>
        <Typography component="h1" variant="h4" align="center">
          판매 등록
        </Typography>
        <br />
        <React.Fragment>
          <React.Fragment>
            <Typography variant="h6" gutterBottom>
              집 정보 등록
            </Typography>
            <Grid container spacing={3}>
              <Grid item xs={12}>
                <Box sx={{ minWidth: 200 }}>
                  <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">지역</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      label="locationv"
                      value={area}
                      onChange={(e) =>
                        setArea(e.target.value)
                      }
                    >
                      <MenuItem value={"대전"}>대전</MenuItem>
                      <MenuItem value={"서울"}>서울</MenuItem>
                    </Select>
                  </FormControl>
                </Box>
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  id="houseAddress"
                  label="집주소"
                  fullWidth
                  variant="standard"
                  onChange={(e) => {
                    setAddress(e.target.value)
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  id="houseprice"
                  label="가격 (ETH)"
                  fullWidth
                  variant="standard"
                  onChange={(e) => {
                    setPrice(e.target.value)
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <Card
                  sx={{ height: '100%', display: 'flex' }}
                >
                  <CardMedia
                    component="img"
                    sx={{
                      // 16:9
                      pt: '5%',
                    }}
                    image={files}
                    alt="random"
                  />
                </Card>
              </Grid>
              <Grid item xs={12} md={6}>
                <Button
                  variant="contained"
                  component="label"
                >
                  사진올리기
                  <input
                    type="file"
                    hidden
