/* eslint-disable react/jsx-pascal-case */
//#region react
import React, { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom";
//#endregion

//#region mui
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
//#endregion

import Notify_Dialog from "./Notify_Dialog";
import RealEstate from '../../contracts/BuyHouse.json';
import Web3 from 'web3';

let web3;
let instance;

function Mainpage_Card(props) {

    const navigate = useNavigate();

    const { cards, user } = props;

    var buy_State = 'buy_Possible'

    // const [sellername] = useState(value[0].name);
    // const [sellerAddress] = useState(value[0].MetaMaskAcc);
    // const [housePrice] = useState(value[0].price);
    // const [houseAddress] = useState(value[0].address);
    // const [locations] = useState(value[0].location);
    // const [buyername] = useState(value[1][0].name);
    console.log(cards)
    console.log(user)
    useEffect(()=>{
        async function load() {        
            web3 = new Web3(Web3.givenProvider || 'http://localhost:8545');
            //setAccounts(await web3.eth.getAccounts());
      
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = RealEstate.networks[networkId];
            
            instance =  new web3.eth.Contract(RealEstate.abi, deployedNetwork.address);
            //console.log(value);
        }
        load();
      },[]);
    
      
    
    // const [accounts, setAccounts] = useState("");
    // async function BuyHouse() {
    //     await instance.methods.buyRealEstate(sellerAddress, locations, sellername, buyername, houseAddress, housePrice).send({
    //         from: accounts[0],
    //         value: web3.utils.toWei(housePrice, "ether"),    //wei
    //         gas: 150000,
    //     })
    // }

    return (
        <Grid container spacing={4}>
            {cards.map((card) => (
                <Grid item xs={5} sm={6} md={4} key={card._id}>
                    <Card
                        sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                    >
                        <CardMedia
                            component="img"
                            sx={{
                                // 16:9
                                pt: '0%',
                                minHeight: 170,
                                maxHeight: 170
                            }}
                            src={card.files}
                            alt="random"
                        />
                        <CardActions>
                            <Box sx={{ flexGrow: 1 }} />
                            <div>
                                {
                                    {
                                        buy_Possible: <Notify_Dialog
                                            warningHead={"구매 확인"}
                                            warningButton={"구매"}
                                            warning={"정말 구매하시겠습니까?"}
                                            value={[card, user]}

                                        ></Notify_Dialog>,
                                        buy_Impossible: <Notify_Dialog
                                            warningButton={"판매완료"}
                                            warning={"판매 완료된 매물입니다."}
                                            value={[card, user]}
                                        ></Notify_Dialog>
                                    }[buy_State]
                                }
                            </div>
                            <Button size="small" onClick={() => {
                                navigate("/post-HouseInfoPage", { state: [card, user] });
                            }}>보기</Button>
                        </CardActions>

                    </Card>
                </Grid>
            ))}
        </Grid>)
}

export default Mainpage_Card;
