/* eslint-disable react/jsx-pascal-case */
//#region react
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
//#endregion

//#region mui
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardMedia from '@mui/material/CardMedia';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
//#endregion

import Notify_Dialog from "./Notify_Dialog";
import RealEstate from '../../contracts/BuyHouse.json';
import Web3 from 'web3';

let web3;
let instance;

function Mainpage_Card(props) {

    const navigate = useNavigate();

    const { cards, user } = props;

    var buy_State = 'buy_Possible'

    const [accounts, setAccounts] = useState("");

    useEffect(() => {
        async function load() {
            web3 = new Web3(Web3.givenProvider || 'http://localhost:8545');
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = RealEstate.networks[networkId];
            setAccounts(await web3.eth.getAccounts());
            instance = new web3.eth.Contract(RealEstate.abi, deployedNetwork.address);
        }
        load();
    }, []);

    async function BuyHouse(card) {

        

        await instance.methods.buyRealEstate(sellerAddress, locations, sellername, buyername, houseAddress, housePrice).send({
            from: accounts[0],
            value: web3.utils.toWei(housePrice, "ether"),    //wei
            gas: 150000,
        })
    }

    const [sellername, setSellerName] = useState();
    const [sellerAddress, setSellerAddress] = useState();
    const [housePrice, setHousePrice] = useState();
    const [houseAddress, setHouseAddress] = useState();
    const [locations, setLocations] = useState();
    const [buyername, setBuyerName] = useState();

    function buyhousetest(card) {

        // const sellerAddress = card.MetaMaskAcc
        // const locations = card.location
        // const sellername = card.name
        // const buyername = user[0].name
        // const houseAddress = card.address
        // const housePrice = card.price    
        
        setSellerName(card.name)
        setSellerAddress(card.MetaMaskAcc)
        setHousePrice(card.price)
        setHouseAddress(card.address)
        setLocations(card.location)
        setBuyerName(user[0].name)
        
        return (
            {
                buy_Possible: <Notify_Dialog
                    warningHead={"구매 확인"}
                    warningButton={"구매"}
                    warning={"정말 구매하시겠습니까?"}
                    onclick={BuyHouse(card)}
                ></Notify_Dialog>,
                buy_Impossible: <Notify_Dialog
                    warningButton={"판매완료"}
                    warning={"판매 완료된 매물입니다."}
                    onclick={BuyHouse(card)}
                ></Notify_Dialog>
            }[buy_State]
        )
    }

    return (
        <Grid container spacing={4}>
            {cards.map((card) => (
                <Grid item xs={5} sm={6} md={4} key={card._id}>
                    <Card
                        sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}
                    >
                        <CardMedia
                            component="img"
                            sx={{
                                // 16:9
                                pt: '0%',
                                minHeight: 170,
                                maxHeight: 170
                            }}
                            src={card.files}
                            alt="random"
                        />
                        <CardActions>
                            <Box sx={{ flexGrow: 1 }} />
                            <div>
                                {
                                    buyhousetest(card)
                                }
                            </div>
                            <Button size="small" onClick={() => {
                                navigate("/post-HouseInfoPage", { state: [card, user] });
                            }}>보기</Button>
                        </CardActions>

                    </Card>
                </Grid>
            ))}
        </Grid>)
}

export default Mainpage_Card;
