import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    BrowserRouter,
    Routes,
    Route
} from "react-router-dom";
import Web3 from 'web3';

// Pages
import MainPage from './component/page/MainPage';
import NewMemberPage from './component/page/NewMemberPage';
import LoginPage from './component/page/LoginPage';
import UserMyPage from "./component/page/UserMyPage";
import HouseInfoPage from "./component/page/HouseInfoPage";
import HouseInfoInsert from "./component/page/registration/HouseInfoInsert";
import HouseInfoUpdate from "./component/page/registration/HouseInfoUpdate"

const web3 = new Web3(Web3.givenProvider || 'http://localhost:7545');
let gameToken;

function App(props) {

    const [accounts, setAccounts] = useState("");
 
    useEffect(() => {
        async  function load(){
            const TokenAddr = "0xab6240dc0496cf7ac1a6ab9b5840ed3fda75266f";
            const remixabi = [
                {
                    "constant": false,
                    "inputs": [
                        {
                            "name": "buyer",
                            "type": "address"
                        }
                    ],
                    "name": "Buy",
                    "outputs": [],
                    "payable": true,
                    "stateMutability": "payable",
                    "type": "function"
                },
                {
                    "inputs": [],
                    "payable": false,
                    "stateMutability": "nonpayable",
                    "type": "constructor"
                },
                {
                    "constant": true,
                    "inputs": [],
                    "name": "CheckBalance",
                    "outputs": [
                        {
                            "name": "",
                            "type": "uint256"
                        }
                    ],
                    "payable": false,
                    "stateMutability": "view",
                    "type": "function"
                },
                {
                    "constant": true,
                    "inputs": [],
                    "name": "MyAddress",
                    "outputs": [
                        {
                            "name": "",
                            "type": "address"
                        }
                    ],
                    "payable": false,
                    "stateMutability": "view",
                    "type": "function"
                },
                {
                    "constant": true,
                    "inputs": [],
                    "name": "owner",
                    "outputs": [
                        {
                            "name": "",
                            "type": "address"
                        }
                    ],
                    "payable": false,
                    "stateMutability": "view",
                    "type": "function"
                }
            ];

    
    
            gameToken = new web3.eth.Contract(remixabi, TokenAddr);   
        }
    
        async function Print(){
            console.log(gameToken);
            const res = await gameToken.methods.MyAddress().call();
            console.log(res);
            const res2 = await gameToken.methods.Buy('0x0e24FcE6F6329C147906bD78733F076Addfd3859').send({
                from: res,
                value: web3.utils.toWei(web3.utils.toBN(1), "ether"),
                gas: 150000,
            });
        }
    
        load();
        Print();
    }, []);

    return (
        <BrowserRouter>
            <Routes>
                <Route index element={<LoginPage />} />
                <Route path="post-NewMemberPage" element={<NewMemberPage />} />
                <Route path="post-MainPage" element={<MainPage/>} />
                <Route path="post-Checkout" element={<HouseInfoInsert/>} />
                <Route path="post-UserMyPage" element={<UserMyPage />} />
                <Route path="post-HouseInfoPage" element={<HouseInfoPage />} />
                <Route path="post-CorrectionForm" element={<HouseInfoUpdate />} />
            </Routes>
        </BrowserRouter>
    );
}

export default App;
