import React, {useEffect, useState} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    BrowserRouter,
    Routes,
    Route
} from "react-router-dom";
import Web3 from 'web3';

// Pages
import MainPage from './component/page/MainPage';
import NewMemberPage from './component/page/NewMemberPage';
import LoginPage from './component/page/LoginPage';
import UserMyPage from "./component/page/UserMyPage";
import HouseInfoPage from "./component/page/HouseInfoPage";
import HouseInfoInsert from "./component/page/registration/HouseInfoInsert";
import HouseInfoUpdate from "./component/page/registration/HouseInfoUpdate"

import BuyHouseContract from "../src/contracts/BuyHouse.json"

function App(props) {

    const [sellername] = useState(location.state[0].name);
    const [sellerAddress] = useState(location.state[0].MetaMaskAcc)
    const [housePrice] = useState(location.state[0].price)
    const [houseAddress] = useState(location.state[0].address)
    const [locations] = useState(location.state[0].location)
    const [buyername] = useState(location.state[1][0].name);

    let web3;

    useEffect(() => {
        async function load() {            
            web3 = new Web3(Web3.givenProvider || 'http://localhost:8545');
            
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = BuyHouseContract.networks[networkId];
            const accounts = await web3.eth.getAccounts();
            setAccounts(accounts);
            
            instance =  new web3.eth.Contract(BuyHouseContract.abi, deployedNetwork.address);
        }
        load();
    }, [location]);

    return (
        <BrowserRouter>
            <Routes>
                <Route index element={<LoginPage />} />
                <Route path="post-NewMemberPage" element={<NewMemberPage />} />
                <Route path="post-MainPage" element={<MainPage/>} />
                <Route path="post-Checkout" element={<HouseInfoInsert/>} />
                <Route path="post-UserMyPage" element={<UserMyPage />} />
                <Route path="post-HouseInfoPage" element={<HouseInfoPage />} />
                <Route path="post-CorrectionForm" element={<HouseInfoUpdate />} />
            </Routes>
        </BrowserRouter>
    );
}

export default App;
